AOS.init();var nav=document.querySelector("nav"),hamburgerBtn=document.querySelector(".navbar-toggler"),headerGradientOverlay=document.querySelector("#gradientOverlay");hamburgerBtn.addEventListener("click",function(){window.pageYOffset<470&&headerGradientOverlay.classList.contains("headergradient")?headerGradientOverlay.classList.remove("headergradient"):headerGradientOverlay.classList.add("headergradient")});var x=setInterval(function(){var e=new Date("April 23, 2022 12:00:00").getTime(),r=(new Date).getTime(),t=e-r;const a=Math.floor(t/864e5),n=Math.floor(t%864e5/36e5),o=Math.floor(t%36e5/6e4),d=Math.floor(t%6e4/1e3);document.querySelector(".day").innerText=a,document.querySelector(".hour").innerText=n,document.querySelector(".minute").innerText=o,document.querySelector(".second").innerText=d},1e3);